def solve(s: str) -> int:
    # Ищем максимальную длину подряд идущих символов,
    # где любые два соседних различны.
    if not s:
        return 0
    best = 1
    cur = 1
    for i in range(1, len(s)):
        if s[i] != s[i-1]:
            cur += 1
        else:
            if cur > best:
                best = cur
            cur = 1
    if cur > best:
        best = cur
    return best

if __name__ == '__main__':
    with open('24.txt', 'r', encoding='utf-8') as f:
        s = f.read().strip()
    print(solve(s))
